import React from 'react'
// import "./molecule.css";
import PropTypes from 'prop-types';

import {
    Box,
    Container,
    Tabs,
    Tab,
    Typography,
    makeStyles,
} from '@material-ui/core';
// import { VerticalTab } from '../../molecules'
import { MainContainer } from '../../atoms';

import '../../molecules/molecule.css'
import Fade from 'react-reveal/Fade';
import { VerticalTab } from '../../molecules';


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}



const useStyles = makeStyles((theme) => ({
    whiteBox: {
        marginTop: '20%',
        paddingBottom: '8%',

    },
    expand: {
        color: 'black',
        width: '50%',
        textAlign: 'left',
        [theme.breakpoints.down('sm')]: {
            width: '100%',

        },
    },
    blueBox: {
        background: '#182AC3',
    },
    blueBoxContainer: {

    },


    //vertical Tabs Box
    mainBox: {
        display: 'flex',
        paddingBottom: '15%',
        [theme.breakpoints.down('sm')]: {
            display: 'none'
        }
    },
    leftBox: {
        width: '50%',
        height: '100%',
        padding: '0% 0%',
        [theme.breakpoints.down('sm')]: {
            padding: '0%',
        },
    },
    tabs: {
        padding: '5%',
    },
    tab: {
        minWidth: '100%',
        color: 'white',
        font: 'normal normal normal 1.2rem Access',
        textTransform: 'none',
        textAlign: 'left',
        paddingLeft: '1rem',
        [theme.breakpoints.down('sm')]: {
            fontSize: '.8rem'
        },
    },
    rightBox: {
        width: '25%',
        position: 'absolute',
        zIndex: '1',
        top: '0%',
        left: '55%',
        // position: 'relative',
        [theme.breakpoints.down('sm')]: {
            display: 'none',
        },
    },
    tabPanel: {
        width: '100%',
        [theme.breakpoints.down('md')]: {
            // top: '-50%',
        },
        [theme.breakpoints.down('sm')]: {
            position: 'static',
            height: 'fit-content',
            width: '80%',
            margin: '0 auto'
        },
        '& .MuiBox-root': {
            padding: '0px'
        },
        '& img': {
            maxWidth: '100%',
            height: '100%'
        },
        '&::before': {
            // content: "''",
            height: '40%',
            width: '30%',
            background: '#D9393E',
            position: 'absolute',
            right: '-8%',
            top: '15%',
            zIndex: '-1',
            [theme.breakpoints.down('sm')]: {
                position: 'static'
            }
        },


    },

}))

export function Expanding() {
    const classes = useStyles();


    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    return (
        <>
            <Box style={{ position: 'relative' }}>
                <MainContainer>
                    <Container fixed className={classes.whiteBox}>
                        <Fade left>
                            <h3 className={classes.expand}>EXPANDING THE CORE  <br /> OF OUR PRIMARY OFFERINGS</h3>
                        </Fade>

                        <Box className={classes.rightBox}>
                            <TabPanel className={classes.tabPanel} value={value} index={0}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/FPGA1.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Ready-to-deploy FPGA solutions designed to meet dynamic market needs & keep up with the changing status quo.
                                    </h6>
                                </Fade>
                            </TabPanel>

                            <TabPanel className={classes.tabPanel} value={value} index={1}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/PCB.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Use case-specific high-speed, multi-layered, multi-CPU PCB Board Designs to help companies cut down the deployment time.
                                    </h6>
                                </Fade>
                            </TabPanel>

                            <TabPanel className={classes.tabPanel} value={value} index={2}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/Software-Development.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Industry leaders in turnkey software solutions. Expertise in Embedded, Android, iOS, Web Applications, and windows-based software development.
                                    </h6>
                                </Fade>
                            </TabPanel>

                            <TabPanel className={classes.tabPanel} value={value} index={3}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/Electronics-manufacturing.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Turnkey EMS provider helping businesses with PCB Assembly, Prototyping, Small & Medium Volume Production.
                                    </h6>
                                </Fade>
                            </TabPanel>


                            <TabPanel className={classes.tabPanel} value={value} index={4}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/Testing-calibration.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Easing quality checks, compliances, and certifications, we help companies build and deploy quality-tested products.
                                    </h6>
                                </Fade>
                            </TabPanel>
                            <TabPanel className={classes.tabPanel} value={value} index={5}>
                                <Fade bottom>
                                    <img src={'./images/homepage-icons/expanding/ML-AI.png'} />
                                </Fade>
                                <Fade bottom>
                                    <h6 style={{ lineHeight: '1.5' }}>
                                        Catering to the healthcare sector through new-age intelligence, offering innovative solutions to ease diagnosis.
                                    </h6>
                                </Fade>
                            </TabPanel>

                        </Box>
                    </Container>
                </MainContainer>
                <Box className={classes.blueBox}>

                    <MainContainer>
                        <Fade right>
                            <Container fixed className={classes.mainBox} >
                                <Box className={classes.leftBox}>
                                    <Tabs
                                        disableRipple
                                        orientation="vertical"
                                        variant="scrollable"
                                        value={value}
                                        onChange={handleChange}
                                        aria-label="Vertical tabs example"
                                        className={classes.tabs}
