import React, { useState } from 'react'

import {
    Box,
    Container,
    makeStyles,
} from '@material-ui/core';
import { GeneralHeading, MainContainer } from '../atoms'
import Bounce from 'react-reveal/Bounce';
import Fade from 'react-reveal/Fade';
import clsx from 'clsx';

const useStyles = makeStyles((theme) => ({
    container1: {
        margin: '10% auto',
    },
    mainTitle: {
        color: 'black',
        textAlign: 'center',
    },
    mainBox: {
        width: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
        [theme.breakpoints.down('sm')]: {
            alignItems: 'center',
        },
    },
    imgBox: {
        width: '50%',
        transition: 'all 800ms ease-in-out',

    },
    boxes: {
        width: '20vw',
        height: '20vw',
        border: '1px solid #DCDCDC',
        boxShadow: '0px 3px 10px #00000017',
        background: 'white',
        textAlign: 'center',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexDirection: 'column',
        padding: "1rem",
        margin: '1.5rem 1rem',
        cursor: 'pointer',
        transition: 'all 300ms ease-in-out',
        [theme.breakpoints.down('sm')]: {
            width: '35vw',
            height: '35vw',
            margin: '5% 0%',
            padding: "1%",
        },
        '& img': {
            width: '100%',
            maxHeight: '100%',
        },
        '&:hover': {
            border: '1px solid white',
            "& $imgBox": {
                transform: 'scale(1.2)',
            },
            "& $red": {
                background: '#DE141A',
                animation: `$redbar 800ms ease-in-out`,
                animationIterationCount: 1,
                animationDuration: '800ms',
            },
        },

    },
    subTitle: {
        color: '#182AC3',
        fontWeight: 'bold',
        [theme.breakpoints.down('xs')]: {
            fontSize: '.8rem'
        },
    },
    reddiv: {
        width: '100%',
        height: '8px',
        overflow: 'hidden',
        [theme.breakpoints.down('sm')]: {
            height: '2px',
            background: 'red'
        },
    },
    red: {
        height: '100%',
        transition: 'all 500ms ease-in-out',

    },
    "@keyframes redbar": {
        "0%": {
            opacity: 0,
            transform: "translateX(-200%)"
        },
        "100%": {
            opacity: 1,
            transform: "translateX(0)"
        },
    },
    "@keyframes redbar1": {
        "100%": {
            opacity: 0,
            transform: "translateX(-200%)"
        },
        "0%": {
            opacity: 1,
            transform: "translateX(0)"
        },
    },
    boxes1: {
        width: '20vw',
        height: '20vw',
        padding: '10%',
        margin: '30px 0px',
        border: '1px solid #DCDCDC',
        boxShadow: '0px 3px 10px #00000017',
        transition: 'all 300ms ease-in-out',
        [theme.breakpoints.down('sm')]: {
            width: '15rem',
            height: '15rem',
            margin: '10px 10px'
        },
        [theme.breakpoints.down('xs')]: {
            width: '14rem',
            height: '14rem',
            marginTop: '15px'
        },
        '& h4': {
            paddingTop: '4%',
            fontWeight: '600'
        },
        '& h6': {
            paddingTop: '3%'
        },
        '&:hover': {
            border: '1px solid white',
        }
    }

}))

export function Squareboxes1({
    maintitle,
    li3,
    subtitle,
    redline,
    data,
    data1
}) {

    const classes = useStyles()

    const [exit, setExit] = useState(false)
    return (
        <MainContainer>
            <Container fixed className={classes.container1}>
                <h2 className={classes.mainTitle}> {maintitle} </h2>
                <br />
                <br />
                <br />
                <Box className={classes.mainBox}>

                    {
                        data ?
                            data.map((item, index) => {
                                return (
                                    <Box
                                        className={clsx(classes.boxes, { [classes.animatedItemExiting]: exit })}
                                    >
                                        <Box className={classes.imgBox}>
                                            <Bounce bottom>
                                                <img src={item.images} alt="Mission Icon" />
                                            </Bounce>
                                        </Box>
                                        <Fade bottom> <h4 className={classes.subTitle}>{item.subtitle} </h4></Fade>
                                        <Box className={classes.reddiv}>
                                            <Box className={classes.red}>
                                            </Box>
                                        </Box>
                                    </Box>
                                )
                            }) :

                            data1.map((item, index) => {
                                return (
                                    <Box
                                        className={clsx(classes.boxes1, { [classes.animatedItemExiting]: exit })}
                                    >
                                        <Box className={classes.imgBox}>
                                            <Bounce bottom>
                                                <img src={item.images} alt="Mission Icon" />
                                            </Bounce>
                                        </Box>
                                        <Bounce bottom><h4 style={{ color: '#182AC3' }}>{item.title}</h4></Bounce>
                                        <Bounce bottom><h6 style={{ color: 'black' }}>- {item.li1}</h6></Bounce>
                                        <Bounce bottom><h6 style={{ color: 'black' }}>- {item.li2}</h6></Bounce>
                                        {
                                            item.li3 ? <Bounce bottom><h6 style={{ color: 'black' }}>- {item.li3}</h6></Bounce> : ''
                                        }
                                    </Box>
                                )
                            })

                    }



                </Box>
            </Container>
        </MainContainer>
    )
}
