import React from 'react'
import { makeStyles, Container, Box, Grid, Button } from '@material-ui/core'
import { PositionImageReverse, Paragraphs, MainContainer } from "../atoms"
import Bounce from 'react-reveal/Bounce';


const useStyles = makeStyles((theme) => ({
    qualityContainer: {
        height: 'fit-content',
        margin: '15% auto',
        width: '100%'
    },
    bigHead: {
        color: 'black',
        textAlign: 'center',
        fontSize: '3rem',
        marginTop: '2%'
    },
    BoxLeft: {
        width: '50%',
        display: 'flex',
        alignItems: 'center',
        [theme.breakpoints.down('sm')]: {
            width: '100%'
        },
    },
    btnBox: {
        width: '100%',
        marginTop: '6%',
        display: 'flex',
        justifyContent: 'center',
    },
    smallBtn: {
        font: 'normal normal normal 1rem Access',
        background: '#182AC3',
        // textTransform: 'none',
        border: 'none',
        color: 'white',
        padding: '2% 3%',
        margin: '0% auto',
        '&:hover': {
            background: 'blue'
        },
        [theme.breakpoints.down('sm')]: {
            fontSize: '.7rem'
        }
    },
    BoxRight: {
        width: '50%',
        alignSelf: 'center',
        [theme.breakpoints.down('sm')]: {
            width: '100%'
        },
    },
    gridContainer: {
        display: 'flex',
        width: '100%',
        [theme.breakpoints.down('sm')]: {
            flexDirection: 'column-reverse',
            marginTop: '20%',
        }
    },
    text: {
        color: 'black',
    }
}))

export function QualityReverse({
    mainheading,
    imageBG,
    heading,
    subhead,
    subtitle,
    subhead1,
    subtitle1,
    subhead2,
    redline,
    uli,
    download,
    href,
    firstli,
    secli,
    thirdli,
    fourthli,
    style,
    style1,
    list,
}) {
    const classes = useStyles()
    return (
        <>
            <MainContainer>
                <Container fixed className={classes.qualityContainer}>
                    <h2 className={classes.bigHead}>{mainheading}</h2>

                    <Box container className={classes.gridContainer}
                        style={style} >

                        <Box className={classes.BoxLeft}>
                            <Bounce left>
                                <Paragraphs
                                    title={heading}
                                    subhead={subhead}
                                    subhead1={subhead1}
                                    subhead2={subhead2}
                                    subtitle={subtitle}
                                    subtitle1={subtitle1}
                                    uli={uli}
                                    list={list}
                                    redline={redline}
                                    firstli={firstli}
                                    secli={secli}
                                    thirdli={thirdli}
                                    fourthli={fourthli}
                                    download={download}
                                    href={href}
                                />
                                {
                                    download ?
                                        <Box className={classes.btnBox}>
                                            <Button
                                                className={classes.smallBtn}
                                                onclick={() => window.open(`${href}`, '_blank', '')}
                                            >
                                                {download}
                                            </Button>
                                        </Box>
                                        : ''
                                }
                            </Bounce>
                        </Box>

                        <Box className={classes.BoxRight}>
                            <Bounce right> <PositionImageReverse imageBG={imageBG} /></Bounce>
                        </Box>
                    </Box>
                </Container>
            </MainContainer>
        </>
    )
}
