import React from 'react'
import { makeStyles, Box } from '@material-ui/core'
import { Buttons, MainContainer } from '../atoms'
import Fade from 'react-reveal/Fade';
import Flip from 'react-reveal/Flip';

const useStyles = makeStyles((theme) => ({
    visitBox: {
        display: 'flex',
        justifyContent: 'center',
        flexDirection: 'column',
        alignItems: 'center',
        border: '2px solid red',
        margin: "5% auto",
        width: '100%',
    },
    textLinkBox: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems:'center',
        width: '100%'
    },
    btmText: {
        color: '#182AC3',
        textAlign: 'center',
        fontWeight: 'bold',
    },
    icon: {
        width: '10rem',
        "& img": {
            width: '100%',
            maxHeight: '100%',
        },
        [theme.breakpoints.down('sm')]: {
            width: '40%',
        },
    },
    btns: {
        width: '28%',
        marginTop: '2%',

        [theme.breakpoints.down('sm')]: {
            width: '40%',
            marginTop: '4%'
        },
        [theme.breakpoints.down('xs')]: {
            width: '60%'
        },
    },

}))

export function Visit({
    goto,
    title,
    style,
    href,
    rightlogo,
}) {

    const classes = useStyles()
    return (
        <Box className={classes.visitBox}>
            <Box className={classes.textLinkBox}>
                <h4 className={classes.btmText} style={style}> {goto}</h4>
                {
                    rightlogo ?
                        <Box className={classes.icon}>
                            <Fade bottom> <img src={rightlogo} alt="rightLogo" /></Fade>
                        </Box>
                        : ''
                }
            </Box>

            <Buttons
                className={classes.btns}
                title={title}
                href={href}
                target="_blank"
                arrowIcon="./images/Icon feather-arrow-right.svg"
            />
        </Box>
    )
}
