import Grid from '@egjs/grid'
import { Box, Button, Container, Input, makeStyles, TextField } from '@material-ui/core'
import React from 'react'
import { Buttons, GeneralHeading, MainContainer, ParagraphsBlue } from '../../atoms'

const useStyles = makeStyles((theme) => ({
    mainBox: {
        display: 'flex',
        justifyContent:'space-between',
        margin: '5% auto',
        [theme.breakpoints.down('sm')]:{
            flexDirection:'column',
            width:'80%'
        }

    },
    leftBox: {
        width: '50%',
        [theme.breakpoints.down('sm')]:{
            width: '100%',
        }
    },
    rightBox: {
        width: '40%',
        [theme.breakpoints.down('sm')]:{
            width: '100%',
        }
    },
    input: {
        margin: '4% 0%',
        width: '100%',
        borderBottom: '2px solid white',
        color: 'white'
    },
    cssLabel:{
        color:'white'
    },
    cssFocused:{
        color:'white'
    },
    btn: {
        background: 'white',
        color: '#182AC3',
        marginTop: '10%',
        font: 'normal normal normal 1.2rem Access',
        textTransform: 'none',
        padding: '.6rem 2rem',
        float: 'right',
        borderRadius: '3px',
        "&:hover":{
            background:'#182AC3',
            boxShadow:'0px 0px 3px white',
            color:'white'
        }
    },
    iconBox: {
        display:'flex',
        alignItems:'flex-end'
    },
    address: {
        marginTop:'2%',
    },
    textBtm: {
        width: '50%',
        margin: '10% auto',
        textAlign:'center',
    }
}))

export function Letstalk() {
    const classes = useStyles()
    return (
        <>
            <MainContainer>
                <Box className={classes.mainBox}>
                    <Box className={classes.leftBox}>
                        <ParagraphsBlue
                            title="Let's Talk"
                            subtitle1="We'll be glad to help you with your queries. Kindly fill-in this enquiry form and we will get back to you within 24 hours."
                            redline
                        />

                        <TextField
                            label="Name"
                            placeholder="John Doe"
                            className={classes.input}
                            variant="standard"
                            color="black"
                            InputLabelProps={{
                                classes: {
                                  root: classes.cssLabel,
                                  focused: classes.cssFocused
                                }
                            }}
                            InputProps={{
                                classes: {
                                  root: classes.cssOutlinedInput,
                                  focused: classes.cssFocused,
                                  notchedOutline: classes.notchedOutline
                                }
                            }}
                        />
                        <TextField
                            label="Email"
                            placeholder="example@email.com"
                            className={classes.input}
                            variant="standard"
                            color="black"
                            InputLabelProps={{
                                classes: {
                                  root: classes.cssLabel,
                                  focused: classes.cssFocused
                                }
                            }}
                            InputProps={{
                                classes: {
                                  root: classes.cssOutlinedInput,
                                  focused: classes.cssFocused,
                                  notchedOutline: classes.notchedOutline
                                }
                            }}

                        />
                        <TextField
                            label="Message"
                            placeholder="message"
                            className={classes.input}
                            variant="standard"
                            color="black"
                            InputLabelProps={{
                                classes: {
                                  root: classes.cssLabel,
                                  focused: classes.cssFocused
                                }
                            }}
                            InputProps={{
                                classes: {
                                  root: classes.cssOutlinedInput,
                                  focused: classes.cssFocused,
                                  notchedOutline: classes.notchedOutline
                                }
                            }}

                        />


                        <Button className={classes.btn}>
                            Send Message
                        </Button>

                    </Box>

                    <Box className={classes.rightBox}>
                        <Box style={{marginTop:'10%'}}>
                            <Box className={classes.iconBox}>
                                <img src={'./images/contactus/Layer_x0020_1.svg'} /> <h4 style={{marginLeft:"5%"}}>INDIA</h4>
                            </Box>
                            <Box className={classes.address}>
                                <h6>Systems Level Solutions (I) Pvt. Ltd. 
                                    <br/>
                                    32, D4, Phase 1,
                                    GIDC Estate V.U. Nagar 
                                    <br/>
                                    Gujarat 388 121, India 
                                    <br/>
                                    Tel.: +91-2692-232 501/502 (Ext. 53)
                                </h6>
                            </Box>
                        </Box>
                        <Box style={{marginTop:'10%'}}>
                            <Box className={classes.iconBox}>
                                <img src={'./images/contactus/Layer_x0020_1-1.svg'} /><h4 style={{marginLeft:"6%"}}>USA</h4>
                            </Box>
                            <Box className={classes.address}>
                                <h6>
                                511 N. Washington Avenue, Marshall <br/>Texas 75670 <br/> Ph: 001-408-852-0067  <br/>Ph: 001-408-705-2339 (Rings in India)
                                </h6>
                            </Box>
                        </Box>
                        <Box style={{marginTop:'10%'}}>
                            <Box className={classes.iconBox}>
                                <img src={'./images/contactus/Layer_x0020_1-2.svg'} /><h4 style={{marginLeft:"11%"}} >UK</h4>
                            </Box>
                            <Box className={classes.address}>
                                <h6>
                                20 Mortlake, 20 Mortlake High Street, <br/> London, SW14 8JN
                                </h6>
                            </Box>
                        </Box>



                    </Box>

                </Box>

                <Box className={classes.textBtm}>
                    <h6>For more information about products and services, please contact us at: info@slscorp.com</h6>
                </Box>
            </MainContainer>
        </>
    )
}
