import { Container, makeStyles, Box } from '@material-ui/core'
import React from 'react'
import { technologystack } from '../../molecules/PortfolioData'
import clsx from 'clsx';
import { MainContainer } from '../../atoms';
import Fade from 'react-reveal/Fade';
const useStyles = makeStyles((theme) => ({
    containers: {
        margin: '10% auto'
    },
    mainBox: {
        display: 'flex',
        justifyContent: 'flex-start',
        flexWrap: 'wrap',
        marginTop: '5%',
        [theme.breakpoints.down('sm')]: {
            flexWrap: 'wrap',
        }
    },
    boxes: {
        width: '25%',
        margin: '3% 0%',
        '& h4': {
            paddingTop: '4%',
            fontWeight: 'bold'
        },
        '& h6': {
            paddingTop: '3%',
            // marginRight:'15%',
            width: "60%",
            lineHeight: '1.8',
            [theme.breakpoints.down('xs')]: {
                width: '80%'
            }
        },
        '& img': {
            width: '2rem',
            height: '.7rem',

        },
        [theme.breakpoints.down('xs')]: {
            width: "50%",
        }
    },
}))

export function Technologystack() {
    const classes = useStyles()
    return (
        <MainContainer>
            <Container className={classes.containers}>
                <Fade bottom><h2 style={{ color: 'black', textAlign: 'center' }}>Technology Stack</h2></Fade>
                <Box className={classes.mainBox}>
                    {
                        technologystack.map((item, index) => {
                            return (
                                <Box className={classes.boxes}>
                                    <Fade bottom> <h4 style={{ color: '#182AC3' }}>{item.number} <img src={'/images/Component 4 – 1.svg'} /></h4></Fade>
                                    <Fade bottom><h4 style={{ color: 'black' }}>{item.title}</h4></Fade>
                                    <Fade bottom><h6 style={{ color: 'black' }}>{item.subtitle}</h6></Fade>
                                </Box>
                            )
                        })
                    }
                </Box>
            </Container>
        </MainContainer>
    )
}
