import React, { useRef } from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './styled.css'
import { makeStyles, Container, Box, Button } from '@material-ui/core'
import { KeyboardArrowLeft, KeyboardArrowRight } from '@material-ui/icons'
import { GeneralHeading, MainContainer } from '../atoms'
import Fade from 'react-reveal/Fade';
import clsx from 'clsx';


const useStyles = makeStyles((theme) => ({
    realText: {
        margin: '15% auto',
        [theme.breakpoints.down('sm')]: {
            margin: "20% auto"
        },
    },
    sliderSec: {
        width: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        marginTop: '3%'
    },
    box: {
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'flex-end',
        flexDirection: 'column',
        [theme.breakpoints.down('sm')]: {

        },
    },
    btn: {
        width: '50px',
        height: '50px',
        display: 'flex',
        alignItems: 'center',
        border: '2px solid #182AC3',
        marginTop: '10%',
        color: '#182AC3',
        "&:hover": {
            background: '#182AC3',
            color: 'white'
        },
        [theme.breakpoints.down('sm')]: {
            width: '40px',
            height: '40px',
        },
    },

    //slider section
    slider: {
        width: '100%',
        justifyContent: 'space-around',
        [theme.breakpoints.down('sm')]: {
            width: '100%'
        }
    },
    card: {
        boxShadow: '0px 0px 20px #0000001F',
        margin: "1% 1rem",
        borderBottom: '3px solid #354B9C',
        background: 'white',
        [theme.breakpoints.down('sm')]: {
            width: '80%'
        }
    },
    cardImage: {
        height: '14vw',
        width: '100%'
    },
    images: {
        width: '100%',
        maxHeight: '100%',
    },
    cardTxt: {
        padding: '5%'
    },
    smallBtn: {
        font: 'normal normal normal .7rem Access',
        background: '#182AC3',
        border: 'none',
        color: 'white',
        padding: '8px 15px',
        '&:hover': {
            background: 'blue'
        },
        [theme.breakpoints.down('sm')]: {
            fontSize: '.6rem'
        }
    },
    cardHead: {
        // font: 'normal normal normal 1.1rem Access',
        color: 'black',
        marginTop: '3%',
        fontWeight: 'bold'

    },
    cardSubHead: {
        color: 'black',
        marginTop: '3%'
    },
    readMore: {
        // font: 'normal normal normal 1.2rem BebasNeue',
        marginTop: '3%',
        "& a": {
            fontFamily: 'BebasNeue',
            color: '#182AC3',

        }
    },
    filters: {
        display: 'flex',
        marginLeft: '70px',
        "& h6": {
            color: '#182AC3',
            fontWeight: 'bold',
            marginRight: '3%',
            cursor: 'pointer',
            "&:hover": {
                color: '#D9393E'
            }
        },
        [theme.breakpoints.down('sm')]: {
            // fontSize:'1rem',
            marginLeft: '0%',

        }
    },
    boxes: {
        margin: "1% 1rem",
        cursor: 'pointer',
        width: '17vw',
        height: '17vw',
        boxShadow: '0px 0px 20px #0000001F',
        background: 'white',
        textAlign: 'center',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        padding: "2rem",
        [theme.breakpoints.down('sm')]: {
            width: '12rem',
            height: '12rem'
        },
        '& h4': {
            color: 'black'
        },
        '&:hover': {
            border: '1px solid white',
            "& $imgBox": {
                transform: 'scale(1.2)',
            },
            "& $red": {
                background: '#DE141A',
                animation: `$redbar 800ms ease-in-out`,
                animationIterationCount: 1,
                animationDuration: '800ms',
            },
        },

    },
    imgBox: {
        height: "100%",
        "& img": {
            transition: 'all 800ms ease-in-out',
            width: '100%',
            maxHeight: '100%',
        },
    },
    iconBox: {
        margin: '15% 0%',
        width: '100%',
        '& img': {
            width: '100%',
            maxHeight: '100%',
        },
        [theme.breakpoints.down('sm')]: {
            width: '90%'
        }
    },

    //red line
    reddiv: {
        width: '100%',
        height: '8px',
        overflow: 'hidden',
        [theme.breakpoints.down('sm')]: {
            height: '2px',
            background: 'red'
        },
    },
    red: {
        height: '100%',
        transition: 'all 500ms ease-in-out',

    },
    "@keyframes redbar": {
        "0%": {
            opacity: 0,
            transform: "translateX(-200%)"
        },
        "100%": {
            opacity: 1,
            transform: "translateX(0)"
        },
    },
    "@keyframes redbar1": {
        "100%": {
            opacity: 0,
            transform: "translateX(-200%)"
        },
        "0%": {
            opacity: 1,
            transform: "translateX(0)"
        },
    },
}))


export function Realtime({
    data,
    data1,
    squarecarddata,
    mainhead,
    maintitle,
    title,
    title1,
    filters,
    image,
    style,
    subtitle,
    subtitle1,
    cases,
    download, // to download certifications
    redline,
    grpicons,
    br
}) {
    const classes = useStyles()

    const sliderRef = useRef();

    const next = () => {
        sliderRef.current.slickNext();
    };
    const previous = () => {
        sliderRef.current.slickPrev();
    };
    {
        var settings = {
            // dots: true,
            arrows: false,
            infinite: true,
            speed: 1000,
            slidesToShow: 3,
            slidesToScroll: 1,
            initialSlide: 0,
            autoplay: true,
            autoplaySpeed: 2000,
            responsive: [
                {
                    breakpoint: 1024,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 1,
                        infinite: true,
                    }
                },
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2,
                        initialSlide: 2
                    }
                },
                {
                    breakpoint: 480,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1,
                        // dots: true,
                    }
                }
            ]
        };

        return (
            <>

                <MainContainer>
                    {/* only slider */}
                    <Container fixed className={classes.realText}>
                        <GeneralHeading
                            maintitle={maintitle}
                            title={title}
                            title1={title1}
                            image={image}
                            subtitle={subtitle}
                            subtitle1={subtitle1}
                            redline={redline}
                            br={br}
                        />
                        {
                            filters ? <Box className={classes.filters}>
                                <h6>All</h6>
                                <h6>NEWS & EVENTS</h6>
                                <h6>CSR</h6>
                                <h6>BLOGS</h6>
                                <h6>CASE STUDIES</h6>
                            </Box> : ''
                        }

                        <Box className={classes.sliderSec}>

                            <Box className={clsx(classes.box)} style={style} >
                                <Box><Fade left><KeyboardArrowLeft className={classes.btn} onClick={previous} /></Fade></Box>
                                <Box><Fade right><KeyboardArrowRight className={classes.btn} onClick={next} /></Fade></Box>
                            </Box>

                            {/* for Solar templtes Blade 4 */}

                            {
                                data1 ?
                                    <Box className={classes.sliderSec}>
                                        {
                                            data1.map((item, index) => {
                                                return (
                                                    <Box>
                                                        <Box className={classes.card} >
                                                            <Fade bottom>
                                                                <Box className={classes.cardImage}>
                                                                    <img className={classes.images} src={item.images} alt="carousel Image" />
                                                                </Box>
                                                            </Fade>
                                                            <Box className={classes.cardTxt}>
                                                                <Fade bottom>
                                                                    {
                                                                        cases ? <Button className={classes.smallBtn} >{item.case}</Button> : ''
                                                                    }
                                                                    <h4 className={classes.cardHead}>{item.head}</h4>
                                                                    <h6 className={classes.cardSubHead}>{item.para}</h6>
                                                                    <h4 className={classes.readMore}>
                                                                        <a href="/" >{item.read}</a>
                                                                    </h4>
                                                                    {
                                                                        download ? <Button className={classes.smallBtn} >Download </Button> : ''
                                                                    }
                                                                </Fade>
                                                            </Box>
                                                        </Box>
                                                    </Box>

                                                )
                                            })
                                        }
                                    </Box>
                                    : ''
                            }

                            <Slider ref={sliderRef} {...settings} className={classes.slider}>

                                {/* CARDS */}

                                {
                                    data ? data.map((item, index) => {
                                        return (
                                            <Box>
                                                <Box className={classes.card} >
                                                    <Fade bottom>
                                                        <Box className={classes.cardImage}>
                                                            <img className={classes.images} src={item.images} alt="carousel Image" />
                                                        </Box>
                                                    </Fade>
                                                    <Box className={classes.cardTxt}>
                                                        <Fade bottom>
                                                            {
                                                                cases ? <Button className={classes.smallBtn} >{item.case}</Button> : ''
                                                            }
                                                            <h4 className={classes.cardHead}>{item.head}</h4>
                                                            <h6 className={classes.cardSubHead}>{item.para}</h6>
                                                            <h4 className={classes.readMore}>
                                                                <a href="/" >{item.read}</a>
                                                            </h4>
                                                            {
                                                                download ?
                                                                    <Button
                                                                        className={classes.smallBtn}
                                                                        href={item.href}
                                                                        onclick={() => window.open(`${item.href}`, '_blank')}
                                                                        target="_blank"
                                                                    >
                                                                        {download}
                                                                    </Button> : ''
                                                            }
                                                        </Fade>
                                                    </Box>
                                                </Box>
                                            </Box>

                                        )
                                    }) : ''
                                }


                                {
                                    squarecarddata ? squarecarddata.map((item, index) => {
                                        return (
                                            <Box >
                                                {
                                                    item.link ?

                                                        <Box>
                                                            <a href={item.link} style={{ textDecoration: 'none' }} target="_blank">
                                                                <Box className={classes.boxes}>
                                                                    <Box className={classes.imgBox}>
                                                                        <img src={item.images} alt="Mission Icon" />
                                                                    </Box>
                                                                    <h4>{item.subtitle} </h4>
                                                                </Box>
                                                                <Box className={classes.reddiv}>
                                                                    <Box className={classes.red}>
                                                                    </Box>
                                                                </Box>
                                                            </a>
                                                        </Box>
                                                        :

                                                        <Box className={classes.boxes}>
                                                            <Box className={classes.imgBox}>
                                                                <img src={item.images} alt="Mission Icon" />
                                                            </Box>
                                                            <h4>{item.subtitle} </h4>
                                                        </Box>
                                                }
                                            </Box>
                                        )
                                    }) : ''
                                }



                            </Slider>
                        </Box>
                        {
                            grpicons ? <Box className={classes.iconBox}>
                                <Fade bottom><img src={grpicons} /></Fade>
                            </Box> : ''
                        }


                    </Container>

                </MainContainer>
            </>
        )
    }
}
